    <script>
        // Cart Management
        class CartManager {
            constructor() {
                this.badge = document.getElementById('cartBadge');
                this.init();
            }

            init() {
                this.updateCartCount();
            }

            updateCartCount() {
                fetch('/Cart/GetCartCount')
                    .then(response => response.json())
                    .then(data => {
                        this.setBadgeCount(data.count);
                    })
                    .catch(error => {
                        console.error('Error updating cart count:', error);
                        // Fallback to 0 if error
                        this.setBadgeCount(0);
                    });
            }

            setBadgeCount(count) {
                if (this.badge) {
                    this.badge.textContent = count;

                    if (count > 0) {
                        this.badge.classList.add('sh");
                WriteLiteral(@"ow');
                        // Add pulse animation when count changes
                        this.badge.style.animation = 'none';
